mapper.delete:
DELETE
FROM Works


        AND id = #{id}


        AND author = #{author}

mapper.select:
SELECT id, author, create_time, title, content
FROM Works


        AND id = #{id}


        AND author = #{author}

SELECT id, author, create_time, title, content
FROM Works,
     UserRelation
WHERE follower_username = #{followerUsername}
  AND following_username = author;
