mapper.delete:
DELETE
FROM UserRelation
WHERE following_username = #{followingUsername}
  AND follower_username = #{followerUsername};

mapper.select:

SELECT COUNT(*)
WHERE followingUsername = #{followingUsername}
  AND followerUsername = #{followerUsername} ;

SELECT following_username
FROM UserRelation
WHERE follower_username = #{followerUsername};

SELECT follower_username
FROM UserRelation
WHERE following_username = #{followingUsername};
